sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

// 	gs.info("MM-leaderboard: qs: "+ request.queryString);
// 	gs.info("MM-leaderboard: url: "+ request.url);
// 	gs.info("MM-leaderboard: headers: "+ JSON.stringify(request.headers));

	var queryParams = request.queryParams; 
	var showTop = queryParams.top; 
	var responseType = (queryParams.text && queryParams.text[0].contains("keep"))?"in_channel":"ephemeral";


	if (!showTop){
		showTop = 20;
	}

	var topList = {};

	var points = new GlideRecord('x_snc_slack_points_slack_user');
	points.orderByDesc('points');
	points.setLimit(showTop);
	points.query();

	var rank = 0;
	var topListTable = "---\n"+
		"#### current Points Leaders\n"+
		"| Points              | User         |\n"+
		"|:--------------------|:-------------|\n";

	while(points.next()){
		rank ++;

		topList[rank.toString()] = {
			points : points.points.toString(),
			user_id : points.user_id.toString(),
			user_name : points.user_name.toString()
		};

		topListTable += "| "+topList[rank.toString()].points+" | @"+topList[rank.toString()].user_name+" |\n";
	}
	topListTable += "---\n";

// 	return topList;
	response.setContentType('application/json');
	response.setStatus(200);
	responseObject = {
		response_type: responseType, 
		text: topListTable
	};
	var writer = response.getStreamWriter();
	writer.writeString(JSON.stringify(responseObject));

	return;

})(request, response);
